}{}\begin{equation*} \mathrm{NPR}_{y}(d) = \frac{|\lbrace i \in P | i_{\mathrm{Year}}=y \mbox{ and } i_{\mathrm{Disease}}=d\rbrace |}{|\lbrace i \in P | i_{\mathrm{Year}}=y\rbrace |} \end{equation*}\end{document}
